mapper.select:
select b_number, b_subject, b_reg_date, user_id, b_content, b_category, b_readcount, b_recommendcount, rownum
from (select b_number, b_subject, b_reg_date, user_id, b_content, b_category, b_readcount, b_recommendcount, rownum r from 
(select * from board order by b_reg_date desc)
 order by b_number desc)
board where r >= #{start_writing_num} and r <= #{end_writing_num}

and b_category = #{b_category}

	 select b_number, b_subject, b_reg_date, user_id, b_content, b_category, b_readcount, b_recommendcount, rownum
	  from (select b_number, b_subject, b_reg_date, user_id, b_content, b_category, b_readcount, b_recommendcount, rownum 
	 from (select * from board order by b_reg_date desc) order by b_number desc)
    where rownum >= #{start_writing_num} and rownum <= #{end_writing_num} and b_category = #{b_category}

select b_number, b_subject, b_reg_date, user_id, b_content, b_category, b_readcount, b_recommendcount
from (b_number, b_subject, b_reg_date, user_id, b_content, b_category, b_readcount, b_recommendcount, rownum r) 
where b_category like #{b_category}
order by b_number
