mapper.select:
select p.user_id user_id, password, 
user_email, user_phone, is_hellinder_open, is_withboard, location 
from users_profile p, users_register r where p.user_id=r.user_id and p.user_id=#{user_id}


select workout_key from (select rownum r, workout_key from 
(select workout_key from hellinder order by WORKOUT_REG_DATE desc)
) where r > #{last}-6 and r <= #{last}
